import React,{useState} from 'react'

function Forum() {
  const [content, setcontent] = useState({
    Blogtitel:"",
    Id:0,
    
    Comment:[ {
    Date:"",
    Comment:"",
    commentby:""
    }]
    }
    );
  
  const [title, settitle] = useState('')  
  const [author, setauthor] = useState('')
  const [date, setdate] = useState('')
  const [catogery, setcatogery] = useState('')
  const [commnet, setcommnet] = useState('')
  return (
    <div className='p-5'>
        <div class="input-group mb-3">
           <label class="form-label p-3">Title</label>      
           <input type="text" class="form-control" value={title} onChange={(e)=>settitle(e.target.value)} />
        </div>
        <div class="input-group mb-3">
           <label class="form-label p-3">Author</label>      
           <input type="text" class="form-control" value={author} onChange={(e)=>setauthor(e.target.value)} />
        </div>
        <div class="input-group mb-3">
           <label class="form-label p-3">Post Date</label>      
           <input type="text" class="form-control" value={date} onChange={(e)=>setdate(e.target.value)}  />
        </div>
        <div class="input-group mb-3">
           <label class="form-label p-3">Post Catogery</label>      
           <input type="text" class="form-control" value={catogery} onChange={(e)=>setcatogery(e.target.value)}  />
        </div>
        <div class="input-group mb-3">
           <label class="form-label p-3">Content</label>    
           <textarea class="form-control"value={commnet} onChange={(e)=>setcommnet(e.target.value)} ></textarea>  
        </div>
        
        <button type="button" class="btn btn-primary">Submit</button>
    </div>
  )
}

export default Forum
