import React, { useState } from 'react'
import { useDispatch } from 'react-redux'
import { Link } from 'react-router-dom'
import { AddLike } from '../Actions/Actions'

function Card(props) {
    console.log(props.card.Blogtitel)
    const [liked, setliked] = useState(0)
    const dispatch=useDispatch();
    const handler=()=>{
       
        dispatch(AddLike(props.card.Id));
        setliked(liked+1);
        
        
    }
  return (
    <div>
        <div className="card m-3" style={{width: "18rem"}}>
  <img src="static/card/img1.png" className="card-img-top" alt="..."/>
  <div className="card-body">
    <h5 className="card-title">{props.card.Blogtitel}</h5>
    <p className="card-text">{props.card.Comment[0].Comment}</p>
    <button onClick={handler} className="btn btn-primary">Like</button>
    <div>{props.card.Likes}</div>
    <Link to={`/fullcard/${props.card.Id}`} className="btn btn-primary">Show</Link>

  </div>
</div>
    </div>
  )
}

export default Card
