import React, { useState } from 'react'
import { useDispatch } from 'react-redux'
import { Link } from 'react-router-dom'
import { AddLike } from '../Actions/Actions'

function Card(props) {
    console.log(props.card.Blogtitel)
    const [likes, setlikes] = useState(0)
    const dispatch=useDispatch();
    const handler=()=>{
       
        dispatch(AddLike(props.card.Id));
        setlikes(likes+1);
        
        
    }
    const openhandler=()=>{
        document.body.addEventListener('click', props.history.push(`/fullcard/${props.card.Id}`));
    }
  return (
    <div>
        <div className="card m-3" style={{width: "18rem"}}>
  
  <div className="card-body">
    <h5 className="card-title">{props.card.Blogtitel}</h5>
    <img src="static/card/img1.png" className="card-img-top" alt="..."/>
    <p className="card-text">{props.card.Comment[0].Comment}</p>
    
    <button onClick={handler} className="fa fa-thumbs-up btn btn-primary ms-1">&emsp;{props.card.Likes}</button>
    <div className='ps-5'></div>
    <Link to={`/fullcard/${props.card.Id}`} className="btn btn-primary">Show</Link>

  </div>
</div>
    </div>
  )
}

export default Card
